algorithm.description:
time_to_target_temp = -1;

! Arguments must be in alphabetical order
function PHASE( ObservedTemperature, Phase, Time) import time_to_target_temp export time_to_target_temp
{

   target_temp = 378.0 378.0 378.0 378.0;
   wait_at_target_temp = 30 30 210 210;
   iphase = floor(Phase + 0.5);

   if (iphase < 0.5)
   {
       iphase = 1;
       time_to_target_temp = -1;
   }

   if (target_temp(iphase-1) <= ObservedTemperature & time_to_target_temp < 0)
       time_to_target_temp = Time;

   if (time_to_target_temp > 0)
   {
     if (Time - time_to_target_temp >= wait_at_target_temp(iphase-1))
     {
       time_to_target_temp = -1;
       iphase = iphase + 1;
       if (iphase > size(target_temp))
           iphase = -1;
     }
   }

   _PHASE = iphase;
}
